package com.devbenadate.workout.ui

import android.content.Intent
import android.content.SharedPreferences
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import com.devbenadate.workout.R
import com.devbenadate.workout.databinding.FragmentProfileBinding
import com.devbenadate.workout.models.LoginResponse

class ProfileFragment : Fragment() {
    lateinit var sharedPrefs: SharedPreferences

    override fun onCreateView(
        binding: FragmentProfileBinding.tvLogout.setOnClickListener {
        val editor=sharedPrefs.edit()
        editor.putString("ACCESS_TOKEN","")
        editor.putString("USER_ID","")
        editor.putString("PROFILE_ID","")
        editor.apply()

        startActivity(Intent(this, LoginResponse::class.java))
        logOutrequest()

    }}
    var binding: FragmentProfileBinding
    inflater: LayoutInflater, container: ViewGroup?,
    savedInstanceState:Bundle?
    //       .inflate(LayoutInflater,from(parent.context),parent,false)
    var image:String = "data:image/jpeg;base64,"
    Picasso.get()
    .load(image)
    .placeholder(R.drawable.ic_baseline_person_24)
    .error(R.drawable.ic_baseline_error_24)
    .resize(200,100)
    .centerCrop()
    .networkPolicy(NetworkPolicy.OFFLINE)
    .into(binding.imgContact)
    ): View? {
        return inflater.inflate(R.layout.fragment_profile,container,fa)
        // Inflate the layout for this fragment
    }
    fun logOutrequest(){
        sharedPrefs.edit().clear().commit()
    }
}


